      <li>
        &#1050;&#1086;&#1084;&#1087;&#1086;&#1085;&#1077;&#1085;&#1090;&#1085;&#1086;&#1077; (&#1084;&#1086;&#1076;&#1091;&#1083;&#1100;&#1085;&#1086;&#1077;) &#1090;&#1077;&#1089;&#1090;&#1080;&#1088;&#1086;&#1074;&#1072;&#1085;&#1080;&#1077; &#1087;&#1088;&#1086;&#1074;&#1077;&#1088;&#1103;&#1077;&#1090; &#1092;&#1091;&#1085;&#1082;&#1094;&#1080;&#1086;&#1085;&#1072;&#1083;&#1100;&#1085;&#1086;&#1089;&#1090;&#1100; &#1080; &#1080;&#1097;&#1077;&#1090; &#1076;&#1077;&#1092;&#1077;&#1082;&#1090;&#1099; &#1074; &#1095;&#1072;&#1089;&#1090;&#1103;&#1093; &#1087;&#1088;&#1080;&#1083;&#1086;&#1078;&#1077;&#1085;&#1080;&#1103;, &#1082;&#1086;&#1090;&#1086;&#1088;&#1099;&#1077; &#1076;&#1086;&#1089;&#1090;&#1091;&#1087;&#1085;&#1099; &#1080; &#1084;&#1086;&#1075;&#1091;&#1090; &#1073;&#1099;&#1090;&#1100; &#1087;&#1088;&#1086;&#1090;&#1077;&#1089;&#1090;&#1080;&#1088;&#1086;&#1074;&#1072;&#1085;&#1099; &#1087;&#1086;-&#1086;&#1090;&#1076;&#1077;&#1083;&#1100;&#1085;&#1086;&#1089;&#1090;&#1080; (&#1084;&#1086;&#1076;&#1091;&#1083;&#1080; &#1087;&#1088;&#1086;&#1075;&#1088;&#1072;&#1084;&#1084;, &#1086;&#1073;&#1098;&#1077;&#1082;&#1090;&#1099;, &#1082;&#1083;&#1072;&#1089;&#1089;&#1099;, &#1092;&#1091;&#1085;&#1082;&#1094;&#1080;&#1080; &#1080; &#1090;.&#1076;.). &#1054;&#1073;&#1099;&#1095;&#1085;&#1086; &#1082;&#1086;&#1084;&#1087;&#1086;&#1085;&#1077;&#1085;&#1090;&#1085;&#1086;&#1077; (&#1084;&#1086;&#1076;&#1091;&#1083;&#1100;&#1085;&#1086;&#1077;) &#1090;&#1077;&#1089;&#1090;&#1080;&#1088;&#1086;&#1074;&#1072;&#1085;&#1080;&#1077; &#1087;&#1088;&#1086;&#1074;&#1086;&#1076;&#1080;&#1090;&#1089;&#1103; &#1087;&#1086;&#1089;&#1088;&#1077;&#1076;&#1089;&#1090;&#1074;&#1086;&#1084; &#1074;&#1099;&#1079;&#1086;&#1074;&#1072; &#1082;&#1086;&#1076;&#1072;, &#1082;&#1086;&#1090;&#1086;&#1088;&#1099;&#1081; &#1085;&#1077;&#1086;&#1073;&#1093;&#1086;&#1076;&#1080;&#1084;&#1086; &#1087;&#1088;&#1086;&#1074;&#1077;&#1088;&#1080;&#1090;&#1100; &#1080; &#1087;&#1088;&#1080; &#1087;